// å¤„ç†è¯·æ±‚
function handleRequest(req) {
    // è¯·æ±‚urlçš„æ•°ç»„
    const urls = [];
    const urlStr = req.url;
    // let urlObj = new URL(urlStr)

    // åŒ¹é…è¯·æ±‚
    if (configs['cdn']) {
        for (let cdn of configs['cdn']) {
            // æ­£åˆ™åŒ¹é…url
            if (cdn['rule'].test(urlStr)) {
                let rule_search = cdn['search'] || cdn['rule']; // å½“searchå­—æ®µä¸å­˜åœ¨æ—¶è®¾ç½®é»˜è®¤å€¼
                if (rule_search == '_') {
                    // å½“ä¸ºè¯­æ³•ç³–æ—¶é‡æ–°èµ‹å€¼ä¸ºrule
                    rule_search = cdn['rule'];
                };
                // éå†æ›¿æ¢
                for (let search_replace of cdn['replace']) {
                    let push_url_str
                    if (search_replace == '_') {
                        // å½“ä¸ºè¯­æ³•ç³–æ—¶é‡æ–°èµ‹å€¼
                        push_url_str = urlStr;
                    } else {
                        push_url_str = urlStr.replace(rule_search, search_replace)
                    };
                    urls.push(push_url_str);
                };
            };
        };
    } else {
        console.warn('[SW] è­¦å‘Š: é…ç½®æœªåŒ…å«cdné…ç½®é¡¹!');
    };

    // å¦‚æœä¸Šæ–¹ cdn éå† åŒ¹é…åˆ° cdn åˆ™ç›´æ¥ç»Ÿä¸€å‘é€è¯·æ±‚(ä¸ä¼šå¾€ä¸‹æ‰§è¡Œäº†)
    if (urls.length) return fetchAny(urls)

    console.debug(`[SW] è¯·æ±‚ ${urlStr} æ²¡æœ‰åŒ¹é…åˆ°ä»»ä½•è§„åˆ™ï¼Œè·³è¿‡æ­¤æ¬¡è¯·æ±‚ã€‚`);

    // è®©swä¸æ‹¦æˆªè¯·æ±‚, æœ‰æ²¡æœ‰æ— æ‰€è°“ã€‚
    return null;
};


// å‘é€æ‰€æœ‰è¯·æ±‚
function fetchAny(urls) {
    // ä¸­æ–­ä¸€ä¸ªæˆ–å¤šä¸ªè¯·æ±‚
    // å…¶å®æ˜¯è·å–å½“å‰æ–¹æ³•å‘èµ·çš„fetchè¯·æ±‚
    // ç„¶ååœ¨ä¸‹æ–‡æ‰“æ–­
    const controller = new AbortController()
    const signal = controller.signal

    // éå†å°†æ‰€æœ‰çš„è¯·æ±‚åœ°å€è½¬æ¢ä¸ºpromise
    const PromiseAll = urls.map((url) => {
        // Promiseçš„æ„é€ å‡½æ•°æ¥æ”¶ä¸¤ä¸ªå‚æ•°ï¼šresolveå’Œrejectï¼ˆå¯ä»¥çœç•¥ï¼‰ã€‚
        // å…¶ä¸­resolveæ˜¯ç”¨æ¥æ ‡è®°ä»£ç æ‰§è¡ŒæˆåŠŸçš„ï¼Œç”¨æ³•ä¸ºresolve(args)ï¼Œ
        // ä¼ è¿›å»çš„å‚æ•°æˆ‘ä»¬åé¢å†è¯´ã€‚ç›¸åï¼Œrejectå°±æ˜¯ç”¨æ¥æ ‡è®°ä»£ç æ‰§è¡Œé”™è¯¯ï¼Œç”¨æ³•ä¸ºreject(args)ã€‚
        return new Promise((resolve, reject) => {
            fetch(url, { signal })
                // è¿”å›å“åº”
                .then(progress)
                // æ£€æŸ¥è¯·æ±‚æ˜¯å¦æˆåŠŸ
                .then((res) => {
                    // å…‹éš†è¯·æ±‚
                    const r = res.clone()
                    if (r.status !== 200) reject(null)
                    controller.abort() // ä¸­æ–­
                    // è¿”å›è¯·æ±‚
                    resolve(r)
                })
                .catch(() => reject(null));
        })
    })

    // åˆ¤æ–­æµè§ˆå™¨æ˜¯å¦æ”¯æŒ Promise.any
    // å¦‚æœä¸æ”¯æŒå°±æ‰§è¡Œä¸Šé¢çš„æ–¹æ³•
    // if (!Promise.any) createPromiseAny()

    // è°å…ˆè¿”å›"æˆåŠŸçŠ¶æ€"åˆ™è¿”å›è°çš„å†…å®¹ï¼Œå¦‚æœéƒ½è¿”å›"å¤±è´¥çŠ¶æ€"åˆ™è¿”å›null
    return Promise.any(PromiseAll)
        .then((res) => res)
        .catch(() => null);
};

function fetchOne(url){
    return fetch(url)
        .then(progress)
        .then((res) => {
            // å…‹éš†è¯·æ±‚
            const r = res.clone()
            // æ£€æŸ¥è¯·æ±‚æ˜¯å¦æˆåŠŸ
            if (r.status !== 200) return null
            // è¿”å›è¯·æ±‚
            return r
        })
}

